import React, { Component } from 'react';
import { connect } from 'react-redux';
import { actionCreators } from './store';
import { CSSTransition } from 'react-transition-group';
import {
	HeaderWrapper,
	Logo,
	Nav,
	NavItem,
	SearchWapper,
	SearchInfo,
	SearchInfoTitle,
	SearchInfoSwitch,
	SearchInfoList,
	SearchInfoItem,
	NavSearch,
	Addition,
	Button,
}from './style.js'

class Header extends Component {
	render() {
		return(
			<HeaderWrapper>
				<Logo/>
				<Nav>
					<NavItem className="left active">Home</NavItem>
					<NavItem className="left">Articles</NavItem>
					<NavItem className="right">Login</NavItem>
					<NavItem className="right">
						<i className="iconfont">&#xe636;</i>
					</NavItem>
					<SearchWapper>
						<CSSTransition
							in={this.props.focused}
							timeout={200}
							classNames="slide">
							<NavSearch
							className={this.props.focused ? 'focused' : ''}
							onFocus={this.props.handleInputFocus}
							onBlur={this.props.handleInputBlur}
							></NavSearch>
						</CSSTransition>
						<i className={this.props.focused ? 'focused iconfont' : 'iconfont'}>&#xe60d;</i>
						{ getListArea(this.props.focused) }
					</SearchWapper>
				</Nav>
				<Addition>
					<Button>Register</Button>
					<Button className="post">
					<i className="iconfont">&#xe670;</i>
					Post
					</Button>
				</Addition>
			</HeaderWrapper>
		)
	}
}

const getListArea = (show) => {
	if(show) {
		return(
			<SearchInfo>
				<SearchInfoTitle>
				hottest
				<SearchInfoSwitch>others</SearchInfoSwitch>
				<SearchInfoList>
					<SearchInfoItem>life</SearchInfoItem>
					<SearchInfoItem>art</SearchInfoItem>
					<SearchInfoItem>music</SearchInfoItem>
					<SearchInfoItem>movie</SearchInfoItem>
					<SearchInfoItem>pet</SearchInfoItem>
					<SearchInfoItem>pet</SearchInfoItem>
					<SearchInfoItem>pet</SearchInfoItem>
				</SearchInfoList>
				</SearchInfoTitle>
			</SearchInfo>
		)
	}else{
		return null;
	}
}



const mapStateToProps = (state) => {
	return {
		focused: state.get('header').get('focused')
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		handleInputFocus() {
			dispatch(actionCreators.searchFocus());
		},
		handleInputBlur() {
			dispatch(actionCreators.searchBlur());
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
