import * as constants from './constants';

const defaultState = {
	focused: false
};

export default (state = defaultState, action) => {
	if(action.type === constants.SEARCH_FOCUSED) {
		return {
			focused: true
		}
	};

	if(action.type === constants.SEARCH_BLURRED) {
		return {
			focused: false
		}
	};
	return state;
}
