import React, { PureComponent } from 'react';
import { connect } from 'react-redux';
import { WriterWrapper, WriterItem, WriterImg, WriterDes } from '../style.js';

class Writer extends PureComponent {
	render() {
		return (
			<WriterWrapper>
				<div className="recommend"> Recommend Writers</div>
				<WriterItem>
					<WriterImg>
						<img className="writer-img" src="/images/book.jpg" />
					</WriterImg>
					<WriterDes>
						<div className="username">maomao</div>
						<div className="description">I love eat</div>
					</WriterDes>
				</WriterItem>
			</WriterWrapper>
		)
	}
}

const mapState = (state) => ({
	list: state.getIn(['home','recommendList'])
})

export default Writer;
