import styled from 'styled-components';
import logoPic from '../../statics/logo.png';

export const HeaderWrapper = styled.div`
	position: relative;
	height: 56px;
	border-bottom: 1px solid #f0f0f0;
`;

export const Logo = styled.a.attrs({
	href: '/'
})`
	positon: absolute;
	left:0;
	top:0;
	display: block;
	float: left;
	width: 125px;
	height: 56px;
	background: url(${logoPic});
	background-size: contain;
`; // click logo will direct to root path

export const Nav = styled.div`
	width: 960px;
	height: 56px;
	margin: 0 auto;
`;

export const NavItem = styled.div`
	padding: 0 15px;
	font-size: 17px;
	line-height: 56px;
	color: #333;
	&.left{
		float: left;
	}
	&.right{
		float: right;
		color: #969696;
	}
	&.active{
		color: #d64d7a;
	}
`;

export const SearchWapper = styled.div`
	position: relative;
	float: left;
	.iconfont{
		position: absolute;
		right: 5px;
		bottom: 5px;
		width: 30px;
		line-height: 30px;
		border-radius: 15px;
		text-align: center;
		&.focused {
			background: #777;
			color: #fff;
		}
	}
`;


export const NavSearch = styled.input.attrs({
	placeholder: 'search'
})`
	width: 160px;
	height: 38px;
	padding: 0 35px 0 20px;
	margin-left: 20px;
	font-size: 14px;
	box-sizing: border-box;
	border: none;
	outline: none;
	border-radius: 19px;
	background: #eee;
	margin-top: 8px;
	color: #777;
	&::placeholder{
		color: #999;
	}
	&.focused{
		width: 240px;
	}
	&.slide-enter{
		transition: all .2s ease-out;
	}
	&.slide-enter-active{
		width: 240px;
	}
	&.slide-exit{
		transition: all .2s ease-out;
	}
	&.slide-exit-active{
		width: 160px;
	}
`;

export const SearchInfo = styled.div`
	position: absolute;
	left: 0;
	top: 56px;
	width: 240px;
	height: 100px;
	padding: 0 20px;
	box-shadow: 0 0 8px rgba(0, 0, 0, .2);
	background: #fff;
`;

export const SearchInfoTitle = styled.div`
	margin-top: 20px;
	margin-bottom: 15px;
	line-height: 20px;
	font-size: 14px;
	color: #969696;
`;

export const SearchInfoSwitch = styled.span`
	float: right;
	font-size: 13px;
`;

export const SearchInfoItem = styled.a`
	display: block;
	float: left;
	line-height: 20px;
	padding: 0 5px;
	font-size: 12px;
	border: 1px solid #ddd
	color: #969696;
	border-radius: 2px;
`;

export const Addition = styled.div`
	position: absolute;
	right: 0;
	top: 0;
	height: 56px;
`;

export const Button = styled.div`
	float: left;
	line-height: 38px;
	border-radius: 19px;
	border: 1px solid #d64d7a;
	font-size: 14px;
	color: #d64d7a;
	margin-right: 20px;
	margin-top: 9px;
	padding: 0 20px;
	&.post{
		color: #fff;
		background: #d64d7a;
	}
	.iconfont{
		padding-right: 5px;
	}

`
