import React from 'react';
import { connect } from 'react-redux';
import { actionCreators } from './store';
import { CSSTransition } from 'react-transition-group';
import {
	HeaderWrapper,
	Logo,
	Nav,
	NavItem,
	SearchWapper,
	SearchInfo,
	SearchInfoTitle,
	SearchInfoSwitch,
	SearchInfolist,
	SearchInfoItem,
	NavSearch,
	Addition,
	Button,
}from './style.js'

const Header = (props) => {
	return(
		<HeaderWrapper>
			<Logo/>
			<Nav>
				<NavItem className="left active">Home</NavItem>
				<NavItem className="left">Articles</NavItem>
				<NavItem className="right">Login</NavItem>
				<NavItem className="right">
					<i className="iconfont">&#xe636;</i>
				</NavItem>
				<SearchWapper>
					<CSSTransition
						in={props.focused}
						timeout={200}
						classNames="slide">
						<NavSearch
						className={props.focused ? 'focused' : ''}
						onFocus={props.handleInputFocus}
						onBlur={props.handleInputBlur}
						></NavSearch>
					</CSSTransition>
					<i className={props.focused ? 'focused iconfont' : 'iconfont'}>&#xe60d;</i>
					<SearchInfo>
						<SearchInfoTitle>
						hottest
						<SearchInfoSwitch>others</SearchInfoSwitch>
						<SearchInfolist>
							<SearchInfoItem>life</SearchInfoItem>
							<SearchInfoItem>art</SearchInfoItem>
							<SearchInfoItem>music</SearchInfoItem>
							<SearchInfoItem>movie</SearchInfoItem>
							<SearchInfoItem>pet</SearchInfoItem>
						</SearchInfolist>
						</SearchInfoTitle>
					</SearchInfo>
				</SearchWapper>
			</Nav>
			<Addition>
				<Button>Register</Button>
				<Button className="post">
				<i className="iconfont">&#xe670;</i>
				Post
				</Button>
			</Addition>
		</HeaderWrapper>
	);
}


const mapStateToProps = (state) => {
	return {
		focused: state.get('header').get('focused')
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		handleInputFocus() {
			dispatch(actionCreators.searchFocus());
		},
		handleInputBlur() {
			dispatch(actionCreators.searchBlur());
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
