import styled from 'styled-components';
import logoPic from '../../statics/logo.png';

export const HeaderWrapper = styled.div`
	position: relative;
	height: 56px;

	border-bottom: 1px solid #f0f0f0;
`;

export const Logo = styled.a.attrs({
	href: '/'
})`
	positon: absolute;
	left:0;
	top:0;
	display: block;
	float: left;
	width: 125px;
	height: 56px;
	background: url(${logoPic});
	background-size: contain;
`; // click logo will direct to root path

export const Nav = styled.div`
	width: 960px;
	height: 56px;
	margin: 0 auto;
`;

export const NavItem = styled.div`
	padding: 0 15px;
	font-size: 17px;
	line-height: 56px;
	color: #333;
	&.left{
		float: left;
	}
	&.right{
		float: right;
		color: #969696;
	}
	&.active{
		color: #d64d7a;
	}
`
