import React, { Component } from 'react';
import {
	HeaderWrapper,
	Logo,
	Nav,
	NavItem,
	NavSearch,
	Addition,
	Button,
	SearchItem
}from './style.js'


class Header extends Component {
	constructor(props) {
		super(props);
		this.state = {
			focusd: false
		}
	}

	render() {
		return(
			<HeaderWrapper>
				<Logo/>
				<Nav>
					<NavItem className="left active">Home</NavItem>
					<NavItem className="left">Articles</NavItem>
					<NavItem className="right">Login</NavItem>
					<NavItem className="right">
						<i className="iconfont">&#xe636;</i>
					</NavItem>
					<SearchItem>
						<NavSearch
						className={this.state.focusd? 'focusd' : ''}
						></NavSearch>
						<i className={this.state.focusd? 'focusd iconfont' : 'iconfont'}>&#xe60d;</i>
					</SearchItem>
				</Nav>
				<Addition>
					<Button>Register</Button>
					<Button className="post">
					<i className="iconfont">&#xe670;</i>
					Post
					</Button>
				</Addition>
			</HeaderWrapper>
		) 
	}
}

export default Header;
